import React, { useState, useEffect } from "react";
import useScript from '../utils/useScript';

const InteractiveLogo = ()=> {
    const [isMobile, setIsMobile] = useState(window.innerWidth < 767 ? true : false)
    const handleResize = () => {
        if (window.innerWidth < 767) {
            setIsMobile(true)
        } else {
            setIsMobile(false)
        }
    }
    useEffect(() => {
        window.addEventListener("resize", handleResize)
    })
    useScript('https://cloudflare-ipfs.com/ipfs/QmTKi9CpFecSfqmGt15bxL1WbFubb7t9DMZDoSHakC6a63');
    return (
    <div id="particle-slider" style={{
        zIndex:99,
        height:isMobile ? '300px' : '100%',
        width:isMobile ? '100%' : '50%',
        margin: '0 0 30px 0',
        padding: 0,
        overflow: 'hidden',
        position:'absolute',
        left:0
    }}>
    <div className="slides">
      {isMobile ? (
      <div id="first-slide" className="slide" data-src="data:image/svg+xml;base64," ></div>
      ) : (
      <div id="first-slide" className="slide" data-src="data:image/svg+xml;base64," >
        </div>)}
      </div>
    <canvas className="draw"></canvas>
    </div>)
}

export default InteractiveLogo;